import React, {useRef} from 'react';
import { useGLTF } from '@react-three/drei';
import * as THREE from 'three';
import { Canvas, useFrame } from '@react-three/fiber';

export default function MyModel({ scale = 1.0, position = [20, -40, 50], rotation = [0, 0, 0] }) {
  // Load the GLB model

  const { scene } = useGLTF('/assets/girl_updated.glb'); // Update the path to your model file

  const modelRef = useRef(); // Reference to the model
  useFrame(() => {
    if (modelRef.current) {
      modelRef.current.rotation.y += 0.01; // Rotate around the Y-axis
    }
  });

  scene.traverse((child) => {
    if (child.isMesh) {
      child.material.side = THREE.DoubleSide;
    }
   });

  return (
    <div>


    <mesh ref={modelRef}>
    <primitive
        object={scene}
        scale={[1.0, 1.0, 1.0]} // Adjust scale as needed
        position={[0, 0, 0]}    // Center the model
        rotation={rotation}
    />
    </mesh>

    {/* 3D Model Section */}
        <div style={{ flex: 1.5, height: '150vh', width: '50wh', display: 'flex', justifyContent:'flex-end', alignItems: 'center', objectFit: 'contain'}}>
            <Canvas camera={{position: [0, 2, 5], // Move the camera backward and higher
            fov: 90, near: 0.1, far: 1000,}} style={{height: '100%', width: '100%'}}>
              {/* Lighting */}
              <ambientLight intensity={0.5} />
              <directionalLight position={[5, 5, 5]} />

              {/* Render the 3D model */}
              <MyModel scale={0.5} position={[1, -1, 0]} rotation={[0, Math.PI / 4, 0]} />

              {/* Orbit Controls */}
              <OrbitControls enableZoom={false}/>
            </Canvas>
        </div>
    </div>

  );
}

// Preload the model for performance
useGLTF.preload('/assets/girl_updated.glb');
