// src/App.js
import React, { useState, useEffect } from 'react';

function App() {
  const [weather, setWeather] = useState(null);
  const [location, setLocation] = useState({ lat: null, lon: null });
  const [error, setError] = useState(null);
  const [currentDay, setCurrentDay] = useState("");

  // Get the current day
  useEffect(() => {
    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const today = new Date();
    setCurrentDay(days[today.getDay()]);
  }, []);

  // Get user's location and fetch weather
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        setLocation({
          lat: position.coords.latitude,
          lon: position.coords.longitude
        });
      }, (err) => {
        setError("Location access denied.");
      });
    } else {
      setError("Geolocation is not supported by this browser.");
    }
  }, []);

  // Fetch weather data using Tomorrow.io
  useEffect(() => {
    if (location.lat && location.lon) {
      const fetchWeather = async () => {
        try {
          const response = await fetch(
            'https://api.tomorrow.io/v4/weather/forecast?location=42.3478,-71.0466&apikey=pBoXWdYZRvGRihkxNndL0fvsPKGRlNCV'
          );
          const data = await response.json();
          setWeather(data.data.timelines[0].intervals[0].values);
        } catch (err) {
          setError("Failed to fetch weather data.");
        }
      };
      fetchWeather();
    }
  }, [location]);

  return (
    <div className="App">
      <h1>Weather App</h1>
      <p>Today is: {currentDay}</p>

      {error && <p>{error}</p>}

      {weather ? (
        <div>
          <h2>Your Location</h2>
          <p>Temperature: {weather.temperature}Â°C</p>
          <p>Weather Code: {weather.weatherCode}</p>
        </div>
      ) : (
        <p>Loading weather...</p>
      )}
    </div>
  );
}

export default App;
