// App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import WeatherComponent from './WeatherComponent'; // Import the WeatherComponent
import Appearance from './Appearance';
import { useState, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
// import Girl_frontend from './assets/Girl_frontend'
import MyModel from './MyModel';
import Calendar from './Calendar';



function App () {

  const [hoveredButton, setHoveredButton] = useState(null);
  const [clickedButton, setClickedButton] = useState(null);

  const handleMouseEnter = (buttonId) => {
      setHoveredButton(buttonId);
  };

  const handleMouseLeave = () => {
      setHoveredButton(null);
  };

  const handleClick = (buttonId) => {
      setClickedButton(buttonId);
  };

  return (
    <Router>
    <div className="App">
      <Appearance/>
      {/* <WeatherComponent /> Use the WeatherComponent here */}
        <div style={{ height: '100vh', width: '100vw' }}>
          <Canvas>
            {/* Lighting */}
            <ambientLight intensity={0.5} />
            <directionalLight position={[5, 5, 5]} />

            {/* Render the 3D model */}
            <MyModel scale={2.0} position={[0, 4, 1]} rotation={[0, Math.PI / 4, 0]} />

            {/* Orbit Controls */}
            <OrbitControls enableZoom={false} />
          </Canvas>
        </div>

      <h2>
        <div className="button-container">
            <Link to="/calendar">
                <button id="button1"
                    className={`pop-text ${
                    hoveredButton === "button1" ? "hover" : ""
                    } ${clickedButton === "button1" ? "click" : ""}`}
                    onMouseEnter={() => handleMouseEnter("button1")}
                    onMouseLeave={handleMouseLeave}
                    onClick={() => handleClick("button1")}
                    >
                    <li>Fits Wrapped</li>
                </button>
            </Link>
            <Routes>
                <Route path="/calendar" element={<Calendar />} />
            </Routes>



            <button id="button2"
            className={`pop-text ${
            hoveredButton === "button2" ? "hover" : ""
            } ${clickedButton === "button2" ? "click" : ""}`}
            onMouseEnter={() => handleMouseEnter("button2")}
            onMouseLeave={handleMouseLeave}
            onClick={() => handleClick("button2")}
            >
            <li>Style Your Day</li>
            </button>
        </div> 
      </h2>
    </div>



    </Router>
  );
}

export default App;
