import React, { useState, useEffect } from 'react';

function App() {
  // Using the useState hook for storing weather data and errors
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);
  // const [weather, setWeather] = useState(null);
  // const [location, setLocation] = useState({ lat: null, lon: null });
  const [currentDay, setCurrentDay] = useState("");

    // Get the current day
    useEffect(() => {
      const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
      const today = new Date();
      setCurrentDay(days[today.getDay()]);
    }, []);




  useEffect(() => {
    // Setting the fetch options
    const options = { method: 'GET', headers: 
{ accept: 'application/json' } };

    // Fetching weather data
    fetch('https://api.tomorrow.io/v4/weather/forecast?location=42.3478,-71.0466&apikey=pBoXWdYZRvGRihkxNndL0fvsPKGRlNCV', options)
      .then(response => response.json())
      .then(data => {
        // Set the weather data to state
        setWeatherData(data);
      })
      .catch(err => {
        // Set the error to state if there is an error
        setError(err);
        console.error(err);
      });
  }, []);

  return (
    <div>
      <main>
        {weatherData && (
          <div>
            <h2>Weather Data</h2>
            <p>{JSON.stringify(weatherData, null, 2)}</p>
          </div>
        )}
        {error && <p>Error fetching data: {error.message}</p>}
      </main>
    </ div>
  );
}

export default App;
