import React, {useState, useEffect} from 'react';
import logoLight from 'public/logo_light_mode.png';
import logoDark from 'public/logo_dark_mode.png';


const Appearance = () => {
     // Check if a theme is saved in localStorage, otherwise default to light
    const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');

    useEffect(() => {
        // Apply the current theme to the body
        document.body.className = theme;
        // Store the selected theme in localStorage
        localStorage.setItem('theme', theme);
    }, [theme]);

    const toggleTheme = () => {
        // Toggle between light and dark
        setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
    };


    return (
        <div>
            <button onClick={toggleTheme}>
            Toggle {theme === 'light' ? 'Dark' : 'Light'} Mode
            </button>

            {/* Dynamically load the logo based on the current theme */}
        <img
            src={theme === 'light' ? logoLight : logoDark}
            alt="Logo"
            style={{ width: '150px' }}
        />


            <h1>Hello, {theme === 'light' ? 'Light' : 'Dark'} Mode!</h1>
        </div>
    );

}

export default Appearance;
