import React, {useState, useEffect} from 'react';
import logoLight from './logo_light_mode.png';
import logoDark from './logo_dark_mode.png';


const Appearance = () => {
     // Check if a theme is saved in localStorage, otherwise default to light
    const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');
    const [isOpen, setIsOpen] = useState(false);

    useEffect(() => {
        // Apply the current theme to the body
        document.body.className = theme;
        // Store the selected theme in localStorage
        localStorage.setItem('theme', theme);
    }, [theme]);

    const toggleTheme = () => {
        // Toggle between light and dark
        setTheme((prevTheme) => (prevTheme === 'light' ? 'dark' : 'light'));
    };

    // Toggle the menu open/close
    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };


    return (
        <div>
            <div className="transparent-box">
                <h1>WELCOME TO YOUR PERSONAL CLOSET</h1>
                <div style={{
                    height: '200px',               // Height of the vertical line
                    margin: '0 20px'               // Spacing around the line
                }}>
                </div>
                <div className="meatball-menu">
                    {/* Meatball icon (three dots) */}
                    <div className="meatball-icon" onClick={toggleMenu}>
                    <span></span>
                    <span></span>
                    <span></span>
                    </div>

                    {/* Dropdown menu */}
                    {isOpen && (
                    <ul className="dropdown-menu">
                    <li>Option 1</li>
                    <li>Option 2</li>
                    <li>Option 3</li>
                    </ul>
                    )}
                </div>
            </div>

            <button
                onClick={toggleTheme}
                style={{
                background: 'none',
                border: 'none',
                padding: 10,
                cursor: 'pointer',
                }}
            >
            {/* Dynamically load the logo based on the current theme */}
            <img
                src={theme === 'light' ? logoDark : logoLight}
                alt="Logo"
                style={{ width: '25px' }}
            />
            </button>
        </div>

    );

}

export default Appearance;
